import axios from 'axios';
import { Employee } from '../models/employee.model';

class EmployeeService {

    private URI: string;

    constructor() {
        this.URI = 'http://localhost:9000/users';
    }

    // returns either an employee object or null if not found
    login(employee: Employee): Promise<Employee> {
        let response = axios.post(this.URI, employee, {withCredentials: true}).then(result => result.data);
        return response;
    }
}

export default new EmployeeService();
