import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import { useDispatch, useSelector } from 'react-redux';
import { useHistory } from 'react-router-dom';
import { EmployeeState } from '../redux/reducer';
import '../css/login.css';
import { SyntheticEvent, useEffect } from 'react';
import { getEmployee } from '../redux/actions';
import EmployeeService from '../services/employee.service';
import { Employee } from '../models/employee.model';
import React, { useRef } from 'react';

function Login() {
    // get state
    const employee = useSelector((state: EmployeeState) => state.employee);
    console.log('Employee state at beginning of Login:', employee);

    // create redux hooks
    const dispatch = useDispatch();
    const history = useHistory();
    let usernameInput = '';
    usernameInput = useRef();
    const passwordInput = useRef();

    //handle form input
    // function HandleFormInput(e: SyntheticEvent) {
    //     let newEmployee = {...employee};

    //     if((e.target as HTMLInputElement).name === 'username') {
    //         newEmployee.username = (e.target as HTMLInputElement).value;
    //     } else {
    //         newEmployee.password = (e.target as HTMLInputElement).value;
    //     }

    //     dispatch(getEmployee(newEmployee));
    // }
    useEffect(() => usernameInput.current && usernameInput.current.focus());
    function submitForm() {
        let newEmployee = {...employee};

        // if((e.target as HTMLInputElement).name === 'username') {
        //     newEmployee.username = (e.target as HTMLInputElement).value;
        // } else {
        //     newEmployee.password = (e.target as HTMLInputElement).value;
        // }

        // dispatch(getEmployee(newEmployee));

        EmployeeService.login(employee).then( (employee: Employee) => {
                dispatch(getEmployee(employee));
                history.push('/dashboard');
        }).catch(() => {
            console.log('User not found!');
        });
    }

    return (
        <Container>
            <Row>
                <Col />
                <Col lg={8}>
                    <Row className="no-gutters" id="login">
                        <Col xs={2} className="formImage"></Col>
                        <Col id="formCol">
                            <h2>Welcome Back</h2>
                            <Form>
                                <Form.Group controlId="formUsername">
                                    <Form.Label>Username</Form.Label>
                                    <Form.Control type="text" name="username" inputRef={usernameInput}/>
                                </Form.Group>
                                <Form.Group controlId="formPassword">
                                    <Form.Label>Password</Form.Label>
                                    <Form.Control type="password" name="password" inputRef={passwordInput}/>
                                </Form.Group>
                                <Button variant="warning" onClick={submitForm}>
                                    Submit
                                </Button>
                            </Form>
                        </Col>
                    </Row>
                </Col>
                <Col />
            </Row>
        </Container>
      );
}

export default Login;
